CREATE DATABASE SPOTIFY

CREATE TABLE Spotify(
	artist_name VARCHAR(255),
	track_name VARCHAR(255) PRIMARY KEY,
	is_explicit BOOLEAN,
	album_release_date DATE,
	genres VARCHAR(255)[],
	danceability numeric,
	valence numeric,
	energy numeric,
	loudness numeric,
	acousticness numeric,
	instrumentalness numeric,
	liveness numeric,
	speechiness numeric,
	key numeric,
	tempo numeric,
	mode numeric,
	duration_ms numeric,
	time_signature numeric,
	popularity numeric
);

